package com.example.lab2android

import android.os.Bundle
import androidx.fragment.app.Fragment
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.navigation.fragment.findNavController
import androidx.recyclerview.widget.LinearLayoutManager
import androidx.recyclerview.widget.RecyclerView

class FirstFragment : Fragment() {
    private lateinit var recyclerView: RecyclerView
    private lateinit var listAdapter: ListAdapter

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        return inflater.inflate(R.layout.fragment_first, container, false)
    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)
        recyclerView = view.findViewById(R.id.recyclerView)
        recyclerView.layoutManager = LinearLayoutManager(context)

        listAdapter = ListAdapter(getData()) { itemData ->
            val action = FirstFragmentDirections.actionFirstFragmentToSecondFragment(itemData)
            findNavController().navigate(action)
        }
        listAdapter = ListAdapter(getData()) { itemData ->
            val action = FirstFragmentDirections.actionFirstFragmentToSecondFragment(itemData)
            findNavController().navigate(action)
        }
        recyclerView.adapter = listAdapter
    }

    private fun getData(): List<ItemData> {
        return listOf(
            ItemData("Title 1", "Description 1", "data:image/jpeg;base64,", "additionalInfo 1"),
            ItemData("Title 2", "Description 2", "data:image/jpeg;base64,", "additionalInfo 2")
        )
    }
}
